/**
 * Represents the possible size options of the DateInput.
 *
 * The possible values are:
 * - `small`
 * - `medium` (Default)
 * - `large`
 * - `none`
 */
export declare type DateInputSize = 'small' | 'medium' | 'large' | 'none';
